package com.example.auth_service.entity;

import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;

import java.time.LocalDateTime;
import java.util.UUID;

@Setter
@Getter
@Entity
@Table(name = "refreshtoken")
public class RefreshTokenEntity {
    @Id
    @GeneratedValue(strategy = GenerationType.AUTO)
    @Column(name = "IdRefreshToken", columnDefinition = "CHAR(36)")
    private UUID idRefreshToken;

    @Column(name = "Keyaccesstoken", length = 36, nullable = false)
    private UUID keyAccessToken;

    @Column(name = "Keyrefreshtoken", length = 36, nullable = false)
    private UUID keyRefreshToken;

    @Column(name = "IdUser", length = 36, nullable = false)
    private UUID idUser;

    @Column(name = "created_at", nullable = false, updatable = false, insertable = false)
    private LocalDateTime createdAt;

    @Column(name = "updated_at", nullable = false, insertable = false, updatable = false)
    private LocalDateTime updatedAt;
}

package com.example.auth_service.entity;

import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;
import org.hibernate.annotations.CreationTimestamp;
import org.hibernate.annotations.UpdateTimestamp;

import java.time.LocalDateTime;
import java.util.UUID;

@Setter
@Getter
@Entity
@Table(name = "refreshtoken")
public class RefreshTokenEntity {
    @Id
    @GeneratedValue(strategy = GenerationType.AUTO)
    @Column(name = "IdRefreshToken", columnDefinition = "CHAR(36)")
    private UUID idRefreshToken;

    @Column(name = "Keyaccesstoken", length = 36, nullable = false)
    private UUID keyAccessToken;

    @Column(name = "Keyrefreshtoken", length = 36, nullable = false)
    private UUID keyRefreshToken;

    @Column(name = "IdUser", length = 36, nullable = false)
    private UUID idUser;

    @CreationTimestamp
    @Column(name = "created_at", nullable = false, updatable = false)
    private LocalDateTime createdAt;

    @UpdateTimestamp
    @Column(name = "updated_at", nullable = false)
    private LocalDateTime updatedAt;
}

